mapper.select:
SELECT *
FROM goods
WHERE

    id = #{id} and


    user_id = #{userId} and


    is_valid = #{isValid} and




        loc0 = #{loc0} and loc1 = #{loc1} and loc2 =#{loc2}




                loc0 = #{loc0} and loc1 = #{loc1}




                        loc0 = #{loc0}






and 1 = 1
LIMIT #{begin}, #{rows}
